        addSound("[2/6] Ava: Well, this was my first time using this magic magnet on people, but...", "thefeathersflypart1-ava-26", true);
        addSound("[1/1] Ava: Or I guess you could just leave, that's cool too...", "thefeathersflypart1-ava-27", true);
        addSound("[1/6] Ava: Ah, finally! I could've taken you there y'know!", "thefeathersflypart1-ava-28", true);
        addSound("[2/6] Ava: Honestly, I just wanted to test something on you... Oh well!", "thefeathersflypart1-ava-29", true);
        addSound("[3/6] Ava: This is the shop! I think we can buy an Enhanced Potential Microchip here. ", "thefeathersflypart1-ava-30", true);
