        addSound("[2/4] Ava: I am NOT giving up! To hell with all this!", "thefeathersflypart1-ava-37", true);
        addSound("[3/4] Ava: Here, go in yourself and ask. I'll cover the cost later.", "thefeathersflypart1-ava-38", true);
